const { Selector } = require('testcafe');

fixture.only ('Actions Demo Tests')
    .page('https://trytestingthis.netlify.app/');

test('TestCafe Actions', async t => {

    await t.maximizeWindow(); // Maximiza a janela do navegador
    // DropDown

    const dropDown = Selector('#option');
    const dropDownOptions = dropDown.find('option'); // .withText('Option 3'); <- dessa forma, buscaria pelo texto do option

    await t.click(dropDown)
    debugger;
    await t.click(dropDownOptions.withText('Option 3'));
    await t.expect(dropDown.value).eql('option 3');
    await t.wait(2000);

    //Upload de arquivo

    await t.setFilesToUpload('#myfile', ['./uploads/arquivoTest01']);
    await t.wait(2000);

    // Scroll page

    await t.scrollBy(0, 500);
    await t.wait(2000);
    await t.scrollBy(0, 500);
    await t.wait(2000);
    await t.scrollBy(0, 500);
    await t.wait(2000);

    // navegation

    await t.navigateTo('https://youtube.com');




});      
    
    